(1, 2, NULL, '<p>I am looking for bright and motivated students who want to pursue research in the following areas</p>\n\n<ol>\n	<li>Himalayan masonry</li>\n	<li>Seismic strengthening of existing/ heritage/ ancient structures</li>\n	<li>Dry stack/ Modern Masonry</li>\n	<li>Traditional rural&nbsp;housing structures (stone, brick, wooden etc., houses</li>\n</ol>\n\n<p>Those interested can mail me your research interest and&nbsp;your latest CV.</p>\n', '1', '1'),
(2, 3, NULL, '<h3><span style=\"color:#330066\"><strong>PHD/M.Tech</strong></span></h3>\n\n<p><span style=\"color:#000000\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:18px\">I am looking for bright and motivated students (PhD and M.Tech.) who want to pursue research in the following areas</span></span></span></p>\n\n<ul>\n	<li>\n	<p><span style=\"color:#000000\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:18px\">Hydroclimatology</span></span></span></p>\n	</li>\n	<li>\n	<p><span style=\"color:#000000\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:18px\">Floods and Droughts</span></span></span></p>\n	</li>\n	<li>\n	<p><span style=\"color:#000000\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:18px\">land Surface Modelling</span></span></span></p>\n	</li>\n	<li>\n	<p><span style=\"color:#000000\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:18px\">AI for Climate</span></span></span></p>\n	</li>\n</ul>\n\n<p><span style=\"color:#000000\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:18px\">Those interested can mail me your research interest and your latest CV.</span></span></span></p>\n\n<h3><span style=\"color:#330066\"><strong><span style=\"font-family:Arial,Helvetica,sans-serif\">Internship</span></strong></span></h3>\n\n<p><span style=\"color:#000000\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:18px\">If you are looking for summer/winter internship at IIT Mandi, please drop an email with you details</span></span></span></p>\n\n<p>&nbsp;</p>\n', '1', '2'),
(3, 3, NULL, '<h3><span style=\"color:#000000\"><strong>PHD/M.Tech</strong></span></h3>\n\n<p><span style=\"color:#000000\">I am looking for bright and motivated students (PhD and M.Tech.) who want to pursue research in the following areas</span></p>\n\n<ul>\n	<li>\n	<p><span style=\"color:#000000\">Hydroclimatology</span></p>\n	</li>\n	<li>\n	<p><span style=\"color:#000000\">Floods and Droughts</span></p>\n	</li>\n	<li>\n	<p><span style=\"color:#000000\">land Surface Modelling</span></p>\n	</li>\n	<li>\n	<p><span style=\"color:#000000\">AI for Climate</span></p>\n	</li>\n</ul>\n\n<p><span style=\"color:#000000\">Those interested can mail me your research interest and your latest CV.</span></p>\n\n<h3><span style=\"color:#000000\"><strong>Internship</strong></span></h3>\n\n<p><span style=\"color:#000000\">If you are looking for summer/winter internship at IIT Mandi, please drop an email with you details</span></p>\n', '1', '1'),
