INSERT INTO `iitmandi_newsstorages` (`id`, `news_slug`, `title`, `description`, `a_link`, `type`, `file_name`, `add_date`, `status`, `is_delete`) VALUES
(6, 'Ms-Neha-Aswal-Recieves-Postdoc-Fellowship', 'Ms. Neha Aswal Recieves Postdoc Fellowship', '<p>The School of Civil and Environmental Engineering is pleased to inform that our PhD scholar, <strong>Ms. Neha Aswal</strong>, has secured postdoctoral fellowship under <strong>BIENVEN&Uuml;E postdoctoral fellowship</strong> Co-funded by the EU Marie Sklodowska-Curie Actions. She will continue her research in condition monitoring of wind turbines with her project, &ldquo;Efficient multi-sensor approach for subsystem assessment targeting smart maintenance of wind farms&rdquo; with Dr. Qinghua Zhang and Dr. Laurent Mevel, I4S, Inria, France. &nbsp;&nbsp;</p>\r\n\r\n<p>Her research at IIT Mandi focused on the development of probabilistic Bayesian filtering-based health monitoring framework for tensegrity structures robust to various uncertainties, carried out in i4S Lab under the supervision of <strong>Dr. Subhamoy Sen.</strong>&nbsp;<br />\r\n&nbsp;</p>\r\n', '', 'news', '1689902180298265.jpg', '2023-07-21 01:16:20', '1', '1'),
(7, 'Mr-Sonu-Kumar-receives-prestigious-Commonwealth-Split-site-Scholarship-2023', 'Mr. Sonu Kumar receives prestigious Commonwealth Split-site Scholarship-2023', '<p>The School of Civil and Environmental Engineering at IIT Mandi is delighted to announce that our Ph.D. research scholar Mr. Sonu Kumar working under the supervision of Dr. Ashutosh Kumar, has been selected for the highly prestigious Commonwealth Split-site Scholarship (CSC-2023) offered by the Government of the United Kingdom. This scholarship will allow Mr. Sonu to conduct his doctoral research for a period of 1 year within the Department of Engineering at Durham University, United Kingdom under the guidance of Professor Ashraf Osman.<br />\r\nHe will be working on &quot;<strong>Developing Foundation Design Guidelines for On-shore Wind Turbine Foundations in Unsaturated Expansive Soil</strong>&ldquo;. Sonu expressed &ldquo;I am quite fortunate to have been awarded this prestigious scholarship which will enable me to pursue a portion of my doctoral research at Durham University. I would also like to extend my heartfelt gratitude to our esteemed professors for fostering such an outstanding academic environment&rdquo;<br />\r\nWe congratulate <strong>Mr. Sonu Kumar </strong>on this remarkable achievement and wish him every success in his research endeavors.<br />\r\n&nbsp;</p>\r\n', '', 'news', '1690256628180511.png', '2023-07-25 03:43:48', '1', '1'),
(8, 'Dr-Shivang-Shekhar-is-presenting-IAStructE', 'Dr. Shivang Shekhar is presenting at IAStructE', '<p>Link to join this event:&nbsp;https://iiit-ac-in.zoom.us/j/92331918963?pwd=V0ZKQnlVY3l3ZSt3VzhvemhvM0tjZz09#success</p>\r\n', 'https://iiit-ac-in.zoom.us/j/92331918963?pwd=V0ZKQnlVY3l3ZSt3VzhvemhvM0tjZz09#success', 'news', '1692440884697803.jpeg', '2023-08-19 10:28:04', '1', '1'),
(9, 'Mr-Shubham-Kumar-visited-Italy-to-attend-Internation-Summer-School', 'Mr. Shubham Kumar visited Italy to attend Internation Summer School', '<p><span style=\"font-size:24px\">The School of Civil and Environmental Engineering, IIT Mandi is delighted to inform that one of our PhD research scholar Mr. Shubham Kumar working under the supervision of Dr. Kala Venkata Uday, has been selected for the &ldquo;LARAM International Summer School on Landslide Risk Assessment and Mitigation, Italy&rdquo; organized by University of Salerno from September 4th to September 15th 2023. He received the International Travel Support (ITS) scheme fellowship provided by SERB, DST and the financial grant of ₹ 1.00 Lakh by IIT Mandi to attend the LARAM Summer School 2023.&nbsp;<br />\r\n<strong>Congratulations to Mr. Shubham Kumar on this remarkable achievement. We wish him every success in his future endeavors !</strong></span><br />\r\n&nbsp;</p>\r\n', '', 'news', '1695449180341000.png', '2023-09-23 06:06:20', '1', '1');
INSERT INTO `iitmandi_programs` (`id`, `name`, `description`, `prgm_img`, `status`, `is_delete`) VALUES
(1, 'UNDERGRADUATE', '<p><span style=\"background-color:#ffffff; color:#5c5c77; font-family:Raleway; font-size:15px\">The School of Civil and Environmental Engineering offers a four-year B.Tech. program in Civil Engineering. The program is designed to provide students with a solid foundation in the fundamentals of Civil Engineering. Course work include a wide variety of courses in the areas of Structural Engineering, Geotechnical Engineering, Water Resources Engineering, Transportation Engineering, Environmental Engineering, and Construction Engineering and Management. For more information on the program&nbsp;</span><a href=\"http://localhost/mandi/iitmandi/uploads/documents/BTech_Ordinance_and_Regulations.pdf\" style=\"box-sizing: inherit; padding: 0px; margin: 0px; font-family: Helvetica, sans-serif; scroll-behavior: smooth; color: rgb(253, 193, 52); text-decoration: none; background-color: rgb(255, 255, 255); outline: 0px; font-size: 15px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: 0.1px; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px;\" target=\"_blank\">Click Here.</a></p>\r\n', '1683744162350511.jpg', '1', '1'),
(2, 'POSTGRADUATE', '<h6 style=\"text-align:start\">M.Tech. (Structural Engineering)</h6>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:start\">The School of Civil and Environmental Engineering offers a two-year M.Tech. program in Civil Engineering with specialization in Structural Engineering. The program is designed to provide students with a solid foundation in the fundamentals of Structural Engineering. The courses includes Design, Analysis and Construction of Structures, Earthquake Engineering, Structural Dynamics, Structural Mechanics, Structural Optimization, Structural Reliability, and Finite Element Methods. For more information on the program&nbsp;<a href=\"http://localhost/mandi/iitmandi/uploads/documents/Mtech_Str_Engg.pdf\" style=\"box-sizing: inherit; padding: 0px; margin: 0px; font-family: Helvetica, sans-serif; scroll-behavior: smooth; color: rgb(253, 193, 52); text-decoration: none; background-color: transparent; outline: 0px;\" target=\"_blank\">Click Here.</a></p>\r\n\r\n<h6 style=\"text-align:start\">M.Tech (By Research)</h6>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:start\">The School of Civil and Environmental Engineering offers a two-year M.Tech (By Research) program in following desciplines:</p>\r\n\r\n<ul style=\"margin-left:0; margin-right:0\">\r\n	<li>Geotechnical Engineering</li>\r\n	<li>Water Resources Engineering</li>\r\n	<li>Environmental Engineering</li>\r\n	<li>Structural Engineering</li>\r\n</ul>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:start\">For more information on the program&nbsp;<a href=\"http://localhost/mandi/iitmandi/uploads/documents/Ordinances_phd_mtech.pdf\" style=\"box-sizing: inherit; padding: 0px; margin: 0px; font-family: Helvetica, sans-serif; scroll-behavior: smooth; color: rgb(253, 193, 52); text-decoration: none; background-color: transparent; outline: 0px;\" target=\"_blank\">Click Here.</a></p>\r\n', '1683744185726429.jpg', '1', '1'),
(3, 'PH.D.', '<p style=\"margin-left:0px; margin-right:0px; text-align:start\">The School of Civil and Environmental Engineering also offers the prestigious doctoral program. Students enrolled in this program earn a degree of Doctor of Philosophy (PhD). Students are expected to make an original research contribution of significance in their chosen area of research. They also must finish a minimum coursework requirement. The courses are chosen suitably by students to aid in their respective research work. Towards the end, students must submit and defend a thesis. The broard research areas are as follows:</p>\r\n\r\n<ul style=\"margin-left:0; margin-right:0\">\r\n	<li>Geotechnical Engineering</li>\r\n	<li>Remote Sensing and GIS</li>\r\n	<li>Environmental Engineering</li>\r\n	<li>Climate Change and Water Resources Engineering</li>\r\n	<li>Structural Engineering</li>\r\n</ul>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:start\">For more information on the program&nbsp;<a href=\"https://iitmandi.ac.in/academics/files/Ordinances_phd_mtech.pdf\" style=\"box-sizing: inherit; padding: 0px; margin: 0px; font-family: Helvetica, sans-serif; scroll-behavior: smooth; color: rgb(253, 193, 52); text-decoration: none; background-color: transparent; outline: 0px;\" target=\"_blank\">Click Here.</a></p>\r\n', '1683744205230294.jpg', '1', '1');
